mapper.select:
SELECT g.id as goodsId, g.title as title, g.seller_id as sellerId, g.category as category, g.auction_status as auctionStatus, g.auction_end_at as auctionEndAt, g.created_at as createdAt, MAX(u.nickname) AS sellerNickname, MAX(ta.title) AS animeTitle, MAX(b.bid_amount) AS currentBidAmount, MAX(gi.file_path) AS mainFilePath, COUNT(w.goods_id) AS wishCount FROM goods g LEFT JOIN user u ON g.seller_id = u.id LEFT JOIN bid b ON b.goods_id = g.id AND b.is_highest = TRUE LEFT JOIN goods_image gi ON gi.goods_id = g.id AND gi.sort_order = 1 LEFT JOIN wishlist w ON w.goods_id = g.id LEFT JOIN tmdb_anime ta ON ta.id = g.anime_id
g.deleted_at IS NULL
and auction_status = #{auctionStatus}
and category = #{category}
and ( g.title LIKE CONCAT('%', #{searchName}, '%') OR ta.title LIKE CONCAT('%', #{searchName}, '%') )
GROUP BY g.id ORDER BY
currentBidAmount DESC
wishCount DESC
g.auction_end_at ASC
LIMIT #{pageOffset}, #{size}

SELECT count(*) FROM goods g LEFT JOIN tmdb_anime ta ON ta.id = g.anime_id
g.deleted_at IS NULL
and auction_status = #{auctionStatus}
and category = #{category}
and ( g.title LIKE CONCAT('%', #{searchName}, '%') OR ta.title LIKE CONCAT('%', #{searchName}, '%') )

select id, goods_id, file_path, sort_order
from goods_image
where goods_id = #{goodsId}
order by sort_order;

mapper.update:
UPDATE goods
    SET deleted_at = NOW()
WHERE id = #{goodsId}
    AND auction_status != 'PROCEEDING'
    AND seller_id = #{loginUserId}

mapper.select:
SELECT auction_status
FROM goods
WHERE id = #{goodsId}
