mapper.insert:
INSERT INTO goods(
    seller_id, anime_id,
    category, title, description,
    start_price, instant_buy_price, auction_status, duration
)
VALUES (
    #{sellerId}, #{animeId},
    #{category}, #{title}, #{description},
    #{startPrice}, #{instantBuyPrice}, #{auctionStatus}, #{duration}
);

mapper.select:
SELECT g.id, g.title, g.seller_id, g.category, MAX(u.nickname) AS sellerNickname, MAX(b.bid_amount) AS currentBidAmount, MAX(g.auction_status) AS auction_status, MAX(gi.file_path) AS mainFilePath, COUNT(w.goods_id) AS wishCount FROM goods g LEFT JOIN user u ON g.seller_id = u.id LEFT JOIN bid b ON b.goods_id = g.id AND b.is_highest = TRUE LEFT JOIN goods_image gi ON gi.goods_id = g.id AND gi.sort_order = 1 LEFT JOIN wishlist w ON w.goods_id = g.id
auction_status = #{auctionStatus}
and category = #{category}
and (title = #{searchName})
GROUP BY g.id
ORDER BY
currentBidAmount DESC
wishCount DESC
g.id DESC
LIMIT #{offset}, #{size}

select id, goods_id, file_path, sort_order
from goods_image
where goods_id = #{goodsId}
order by sort_order;

mapper.update:
UPDATE goods
    SET deleted_at = NOW()
WHERE id = #{goodsId}
    AND (
        auction_status = 'WAIT'
        OR auction_end_at IS NOT NULL
    );

UPDATE goods
SET
    auction_status = #{auctionStatus}


        , auction_end_at = NOW()

WHERE id = #{goodsId}
    AND deleted_at IS NULL
    AND auction_end_at IS NULL;
