mapper.select:
SELECT count(*) FROM goods g LEFT JOIN tmdb_anime ta ON ta.id = g.anime_id
g.deleted_at IS NULL
and auction_status = #{auctionStatus}
and category = #{category}
and ( g.title LIKE CONCAT('%', #{searchName}, '%') OR ta.title LIKE CONCAT('%', #{searchName}, '%') )

mapper.update:
UPDATE goods
    SET deleted_at = NOW()

    seller_id = #{loginUserId}

        AND id = #{goodsId}

    AND auction_status != 'PROCEEDING'
    AND deleted_at IS NULL

mapper.select:
SELECT seller_id
FROM goods
WHERE id = #{goodsId}

SELECT EXISTS(
    SELECT 1
    FROM goods


            seller_id = #{loginUserId}
            AND auction_status = 'PROCEEDING'


            id = #{goodsId}

        AND deleted_at IS NULL

);

SELECT id, stored_filename
FROM goods_image
WHERE id IN

    #{id}
;

mapper.delete:
DELETE FROM goods_image
WHERE id IN

    #{id}
;

update.foreach:
UPDATE goods_image
SET sort_order = #{img.sortOrder}
WHERE id = #{img.imageId}
AND goods_id = #{goodsId}

mapper.select:
SELECT COALESCE(MAX(sort_order), 0) + 1
FROM goods_image
WHERE goods_id = #{goodsId}
