mapper.select:
SELECT wh.id, goods_id, transaction_type, amount, description, wh.created_at FROM wallet_history wh INNER JOIN coin_wallet cw ON cw.id = wh.wallet_id WHERE cw.user_id = #{loginUserId}
AND wh.created_at >= #{walletHistory.startDate}
AND wh.created_at < DATE_ADD(#{walletHistory.endDate}, INTERVAL 1 DAY)
ORDER BY wh.created_at DESC LIMIT #{walletHistory.size} OFFSET #{walletHistory.offset};

mapper.update:
UPDATE coin_wallet
SET balance = balance + #{coinAmount}
WHERE user_id = #{loginUserId}

mapper.insert:
INSERT INTO wallet_history (
    wallet_id,
    transaction_type,
    amount,
    description
)
VALUES (
    (SELECT id FROM coin_wallet WHERE user_id = #{userId}),
    #{transactionType},
    #{amount},
    #{description}
);
